INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nhẹ ', 'Dumbbell twists', 'Nằm phẳng trên một bàn, giữ tạ đứng theo chiều dọc trên đầu (lòng bàn tay hướng về nhau). Giữ khuỷu tay ở trạng thái bán uốn, hạ nhẹ trọng lượng về phía sau và phía trên đầu khi bạn hít thở (như trong hình minh họa). Giữ khuỷu tay tĩnh. Khi bạn đã đưa tạ xuống cùng chiều cao với đầu, mở rộng cánh tay (đưa về phía trước) xuống dưới mức vai và sau đó uốn cơ vai để đưa chúng lên lại đến vị trí khởi đầu. Toàn bộ động tác nên được thực hiện chậm rãi và kiểm soát. Hít thở khi bạn hạ tạ xuống theo kiểu pull-over và thở ra khi bạn hoàn tất động tác.', 'nan', '12', '6', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nhẹ ', 'Butterfly Press', 'Ngồi với khuỷu tay ở trạng thái uốn góc khoảng 90º ở mức giữa ngực. Nắm chặt tay nắm và đẩy bằng khuỷu tay để đưa cánh tay lại gần nhau trước mặt. Hít thở vào khi bạn mở rộng cánh tay ra xa nhất mức linh hoạt của bạn (vì lý do an toàn, bạn nên mở rộng cánh tay ít hơn nếu bạn đang sử dụng trọng lượng nặng), và thở ra khi bạn đóng cánh tay lại.', 'nan', '12', '6', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nhẹ ', 'Machine Bench Press', 'Nằm phẳng trên bàn với chân chạm đất (nếu bàn thấp) hoặc trên một bậc. Nắm chặt các tay nắm, nằm thẳng trên ngực, bằng cách cầm từ trên xuống (quay lòng bàn tay ra ngoài) và đặt tay hơi rộng hơn rộng vai. Ưu tiên đặt ngón tay cái dưới tay nắm. Hít thở vào khi bạn hạ trọng lượng xuống mức giữa ngực và sau đó đưa nó lên theo hướng thẳng đứng. Khuỷu tay của bạn nên giữ vuông góc (và ra xa) cơ thể. Tránh khóa khuỷu tay khi bạn đạt đến đỉnh của động tác. Thở ra khi hoàn tất động tác nâng.', 'nan', '20', '8', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nhẹ ', 'Cable Flyes', 'Nằm phẳng trên bàn với chân chạm đất (nếu bàn thấp) hoặc trên một bậc. Nắm chặt các tay nắm, giữ thẳng trên ngực với khuỷu tay ở trạng thái bán uốn và lòng bàn tay hướng về nhau (nút xương hướng ra ngoài).Hít thở vào khi bạn mở rộng cánh tay để hạ dây cáp xuống mức ngực (phụ thuộc vào sự linh hoạt), giữ khuỷu tay tĩnh, đưa cánh tay lên theo hướng thẳng đứng và về phía trung tâm. Giữ khuỷu tay vuông góc và ra xa khỏi cơ thể, giống như bạn đang ôm ai đó. Thở ra khi hoàn tất động tác nâng.', 'nan', '15', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Trung bình', 'Dumbbell press', 'Nằm phẳng (nằm ngửa) với đầu và lưng được hỗ trợ bởi bàn và chân chạm đất (nếu bàn thấp) hoặc trên một bậc. Nắm chặt các tạ đứng theo chiều dọc trên ngực, giữ chúng hơi rộng ra. Tương tự như việc sử dụng thanh tạ, cánh tay nên di chuyển vuông góc với mặt đất. Hít thở vào khi bắt đầu hạ tạ xuống đến mức ngực (phụ thuộc vào độ linh hoạt) và sau đó đưa chúng lên theo hướng thẳng đứng và hướng về trung tâm. Giữ cánh tay vuông góc và ra xa cơ thể. Thở ra khi hoàn tất động tác nâng.', 'nan', '20', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Trung bình', 'Flyses/Dumbbell flyes', 'Nằm phẳng trên một ghế tập hẹp, hỗ trợ đầu và lưng của bạn bằng cách đặt chân chạm đất (nếu bàn thấp) hoặc ở cuối ghế. Nắm chặt các tạ đứng theo chiều dọc trên ngực, giữ chúng hơi rộng ra, với lòng bàn tay hướng về nhau và khuỷu tay của bạn ở trạng thái bán uốn. Hít thở sâu khi bạn hạ tạ xuống mức ngực (tùy thuộc vào sự linh hoạt của bạn) mà không thay đổi vị trí của khuỷu tay. Đưa tạ lên theo hướng thẳng đứng và hướng về phía trong nhưng không để chúng chạm nhau. Thở ra khi kết thúc động tác. Giữ khuỷu tay ra xa cơ thể như bạn đang om ai đó', 'nan', '30', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Trung bình', 'Push-up', 'Nằm ở tư thế bụng xuống sàn (nằm áp út) với chân chạm nhau và hai tay cách nhau vừa phải qua một chút, ngón tay hướng về bên trong một chút. Bắt đầu với cánh tay duỗi ra và chân thẳng. Hạ cơ thể của bạn cho đến khi ngực chỉ chạm nhẹ vào sàn. Giữ mông cố định. Cơ thể của bạn nên cứng như một tấm gỗ khi bạn hạ xuống. Hít thở trong giai đoạn hạ cơ thể (đến khoảng giữa) và thở ra ở cuối giai đoạn đẩy cơ thể lên.', 'nan', '6', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Trung bình', 'Parallel dips', 'Nắm chặt thanh đôi với cách cầm trung bình, với tay ở tư thế trung tính (hoặc tốt hơn là tư thế hình chữ V hướng xuống). Giữ thân trên hơi nghiêng về phía trước và đôi chân gần nhau. Hít thở vào khi bạn hạ cơ thể xuống thẳng đứng, uốn thân về phía trước và đưa chân về sau. Khi bạn hạ xuống, khuỷu tay nên di chuyển về phía sau xa khỏi thân dưới. Đẩy lên lại khi bạn đạt đến điểm thấp nhất của động tác và thở ra', 'nan', '12', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Trung bình', 'Cable crossover', 'Đứng giữa hai dây đàn hồi với đôi chân ở trạng thái bán uốn (ưu tiên là một chân đặt về phía trước hơn một chút) và thân dưới nghiêng về phía trước một cách nhẹ (khoảng 15° đến 45º) để cố định cơ bụng. Bắt đầu với cánh tay duỗi ra và khuỷu tay ở trạng thái bán uốn và đưa hai tay lại gần nhau ở dưới hông, giống như bạn đang ôm ai đó. Giữ khuỷu tay tĩnh suốt động tác. Hít thở khi bạn mở rộng cánh tay và thở ra khi hoàn tất động tác nội đông.', 'nan', '8', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nặng', 'Bench press', 'Nằm phẳng trên ghế dài tập (nằm ngửa), hỗ trợ đầu và lưng, đặt chân chắc chắn trên mặt đất. Định vị cơ thể và nắm chặt thanh tạ, nằm thẳng trên đầu mắt của bạn, với một cách cầm đối xứng (quay lòng bàn tay ra ngoài), tay hơi rộng hơn rộng vai, ưu tiên giữ ngón tay cái dưới thanh tạ. Hít thở trước khi lấy thanh tạ từ chỗ hỗ trợ và đưa nó về phía ngực của bạn. Hạ khuỷu tay ra xa khỏi thân dưới đến khi thanh tạ chỉ hơi trên phần giữa của ngực, sau đó đưa nó lên theo hướng thẳng đứng. Hít thở vào khi hạ thanh tạ và thở ra khi hoàn tất động tác nâng', 'nan', '6', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nặng', 'Incline bench press', 'Ngồi trở lại trên ghế dài tập, đặt ở một góc nghiêng không quá 30 hoặc 45 độ, đặt chân chắc chắn trên mặt đất và hỗ trợ lưng và đầu của bạn. Nắm chặt thanh tạ, nằm thẳng trên trán, bằng cách cầm từ trên xuống (quay lòng bàn tay ra ngoài). Cánh tay phải di chuyển vuông góc với mặt đất và tay bạn nên hơi rộng hơn một chút so với rộng vai. Hít thở vào trước khi nâng thanh tạ khỏi chỗ hỗ trợ. Hạ thanh tạ cho đến khi chạm nhẹ vào phần trên của ngực và sau đó đưa nó lên theo hướng thẳng đứng. Giữ cánh tay ra xa khỏi cơ thể. Thở ra khi hoàn tất động tác nâng.', 'nan', '15', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nặng', 'Decline bench press', 'Nằm đầu nghiên xuống trên một ghế dài nghiêng (giữa 20° và 30º) với chân của bạn bị hạn chế. Nắm chặt thanh tạ, nằm thẳng trên đầu mắt của bạn, với cách cầm từ trên xuống (quay lòng bàn tay ra ngoài). Hít thở vào trước khi nâng thanh tạ khỏi chỗ hỗ trợ và hạ nó cho đến khi chỉ chạm nhẹ vào ngực. Đưa thanh tạ lên theo hướng thẳng đứng. Cánh tay của bạn nên di chuyển vuông góc với mặt đất (xem Hình minh họa), và bạn nên giữ cánh tay ra xa khỏi thân dưới, giống như trong các bài tập trước đó. Thở ra khi hoàn tất động tác nâng.', 'nan', '20', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nặng', 'Dumbbell pull-over', 'Nằm phẳng trên một bàn với đầu ở mép. Nắm chặt tạ đứng theo chiều dọc với thanh tạ chạy qua hình tam giác được tạo ra bởi ngón cái và ngón áp út. Với khuỷu tay ở trạng thái bán uốn, hạ nhẹ trọng lượng về phía sau và phía trên đầu khi bạn hít thở sâu. Giữ khuỷu tay tĩnh. Bạn nên cảm nhận sự căng trên cơ ngực và sự mở rộng của ngực. Đưa tạ lên lại theo chiều dọc trên đầu mắt khi cơ ngực co bóp. Thở ra khi hoàn tất động tác nâng.', 'nan', '10', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nặng', 'Rolling Dumbbell Push-up', 'Vị trí tương tự như khi thực hiện "push-up" nhưng được hỗ trợ bằng hai tạ và khuỷu tay ở trạng thái bán uốn. Mở rộng cánh tay khi bạn lăn tạ ra phía ngoài. Động tác này tương tự như khi thực hiện bài tập flyes. Giữ động tác và co bóp cơ ngực để đưa tạ về lại vị trí bắt đầu. Hít thở khi bạn xuống và thở ra khi bạn nâng lên.', 'nan', '10', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nặng', 'One-handed Lateral Barbell Lift', 'Đứng giữ thanh tạ có trọng lượng ở một đầu, song song với sàn đất. Nắm chặt phần có trọng lượng bằng cách cầm từ dưới lên (quay lòng bàn tay ra ngoài), và nắm phần còn lại bằng cách cầm từ trên xuống (quay lòng bàn tay ra trong). Tạo ra một tay đứng im, hành động như một trục xoay trong khi tay kia nâng phần có trọng lượng theo một đường cung cho đến khi thanh tạ đứng thẳng. Sau khi hoàn tất động tác, nhẹ nhàng hạ thanh tạ về vị trí bắt đầu. Hít thở vào khi bạn hạ thanh tạ và thở ra khi bạn nâng lên.', 'nan', '10', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Ngực (Chest)', 'Nặng', 'Floor Dumbbell Pull-Over', 'Bắt đầu từ vị trí giống như trong "Dumbbell push up" nhưng với bàn tay đặt phía trước. Lăn tạ về phía trước để hạ thân trên xuống gần mặt đất và đưa nó trở lại để nâng lên và trở về vị trí khởi đầu. Hít thở vào khi bạn xuống, sau đó giữ hơi và thở ra khi hoàn tất động tác nâng lên.', 'nan', '6', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Nhẹ ', 'Chin / Chin-up', 'Dùng cánh tay nắm chặt thanh ngang với cách cầm từ trên xuống (nút xương hướng về phía sau). Đặt tay hơi rộng hơn một chút so với rộng vai. Sử dụng cơ lưng để kéo phần trên của ngực lên đến mức thanh ngang và đồng thời hơi uốn cơ thể về phía sau một cách nhẹ. Để chân thả xuống thẳng hoặc có thể chéo chúng. Bạn cũng có thể uốn đầu gối nếu muốn. Hít thở vào trong nửa đầu của chuyển động hạ và thở ra khi bạn đạt đến đỉnh', 'nan', '6', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Nhẹ ', 'Machine chin-up/Assisted pull-up', 'Quỳ trên bàn (tùy thuộc vào thiết kế) với phần còn lại của cơ thể ở cùng tư thế như Chin-ups cơ bản (Bài tập 1). Giữ lưng thẳng và sử dụng tư thế cầm từ trên xuống với tay hơi rộng hơn vai. Kéo cơ thể lên đồng thời uốn lưng một chút để mở rộng ngực và nghiêng cơ thể về sau một chút. Khuỷu tay nên đi xuống theo đứng đứng và song song với cánh cơ thể. Máy này hầu như luôn cho phép bạn kéo lên theo hướng dọc vượt qua các tay cầm, thường được tách rời. Hít thở trong nửa đầu của động tác hạ và thở ra khi bạn đạt đến đỉnh. Kỹ thuật thở này có thể đảo ngược, tùy thuộc vào kỹ thuật của bạn.', 'nan', '10', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Nhẹ ', 'One-arm dumbbell row/bent-over dumb', 'Đứng gối với một đầu gối trên một bàn phẳng, hỗ trợ thân trên bằng tay ở cùng một bên. Nắm chặt một tạ dài bằng tay còn lại với cách nắm trung lập. Giữ chân còn lại thẳng hoặc hơi uốn với bàn chân chạm đất. Chân này nên nghiêng đường chéo so với cơ thể và hơi đằng sau nó (tùy thuộc vào chiều cao của bạn và độ cao của bàn), giữ cho thân trên ngang và thẳng hàng. Bắt đầu từ điểm thấp nhất, cơ lưng dưới (latissimus dorsi) của bạn sẽ giúp bạn căng và kéo tạ lên đến eo (như việc bạn đang cưa một khúc gỗ). Giữ khuỷu tay gần thân và cánh tay vuông góc với sàn. Hít thở sâu ngay trước khi bạn đưa tạ lên, giữ hơi ở đỉnh động tác và thở ra khi bạn hạ trọng lượng.', 'nan', '6', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'Bent-over barbell row', 'Đứng với đôi chân hơi rộng và lưng thẳng, hông uốn khoảng 45º, đầu gối hơi uốn. Giữ thân trên cơ địa chặt chẽ. Nắm chặt thanh tạ bằng cách cầm từ trên xuống (lòng bàn tay hướng xuống), tay hơi rộng hơn một chút so với rộng vai. Kéo thanh tạ lên đến đỉnh bụng, giữ cho khuỷu tay mở rộng. Hít thở sâu ngay trước khi nâng trọng lượng và thở ra khi hạ thanh tạ xuống.', 'nan', '8', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'T-Bar Row', 'Đứng với đôi chân hơi rộng và thân trên thẳng, hông uốn khoảng 45º và đầu gối hơi uốn. Giữ cơ thể chặt chẽ trong vị trí. Nắm chặt thanh T bằng cách cầm từ trên xuống (lòng bàn tay hướng xuống). Kéo thanh T lên đến đỉnh bụng, giữ cho khuỷu tay mở rộng. Bạn có thể duỗi thân trên một chút cùng một lúc. Do vị trí của bài tập này, quan trọng để hít thở sâu ngay trước khi bạn hạ trọng lượng, sau đó giữ hơi và thở ra qua phần cuối cùng của động tác nâng mà không phải thở hết hơi.', 'nan', '12', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'Dumbbell pull-over', 'Nằm ngửa trên một bàn với đầu bạn nằm ở mép. Nắm chặt một tạ đứng giữa hai tay của bạn với lòng bàn tay chạm vào phía trong của đĩa trên cùng. Giữ cho khuỷu tay hơi uốn, hãy từ từ hạ trọng lượng lên trên và phía sau đầu khi bạn hít thở sâu. Giữ cho khuỷu tay ở vị trí ban đầu. Đưa trọng lượng về vị trí xuất phát khi bạn co cơ lats. Thở ra khi kết thúc động tác.', 'nan', '12', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'One-arm lateral pull', 'Đứng bên cạnh một thanh ngang nằm ngang, hoặc tốt hơn là dọc. Nắm chặt thanh bằng một tay và đặt tay còn lại lên hông. Tự mình đưa cơ thể chệch xa thanh ngang, giữ cơ thể cứng và không di chuyển chân. Sau đó, kéo trở lại bằng cách sử dụng cánh tay, đưa khuỷu tay về phía xương sườn. Hít thở trong nửa đầu của động tác và thở ra trong nửa thứ hai.', 'nan', '20', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'Straight-arm barbell extensions', 'Tư thế tương tự như Bent-over Barbell Row. Tuy nhiên, lần này bạn cần giữ thanh tạ phía sau chân (ngay dưới gần cuối đầu gối phía sau) và nâng lên bằng cách duỗi vai ra phía sau cơ thể. Hít thở khi bạn bắt đầu nâng trọng lượng, sau đó giữ hơi cho đến khi bạn hoàn thành động tác và hạ trọng lượng xuống lại.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'Machine lat pull-down', 'Ngồi với lưng thẳng và nắm chặt cánh tay qua phía trên (nút xương hướng về sau). Kéo xuống theo hướng dọc, giữ cho khuỷu tay gần cạnh cơ thể. Phạm vi chuyển động nên từ tay duỗi ra đến khi co bó, gần giới hạn ở phía dưới. Hít thở khi bạn nâng cánh tay lên và thở ra khi hạ chúng dọc theo thân dưới, mà không thở hết không khí. Kỹ thuật thở này có thể đảo ngược nếu bạn đang sử dụng trọng lượng nhẹ.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'Seated cable row', 'Ngồi phía trước máy cáp với đầu gối hơi uốn và chân chắc chắn trên các đĩa. Nắm chặt thanh bằng cách giữ tay ở tư thế trung lập với cánh tay hầu như đầy đủ duỗi ra. Kéo về phía bụng đồng thời mở rộng ngực để co bó cơ lats. Sử dụng lưng dưới để kiểm soát chuyển động của cơ thể, mà nên ngắn (đồng hành với cú kéo). Hít thở khi bạn thả cáp để hạ trọng lượng và thở ra khi bạn kéo để nâng nó lên. Bạn có thể đảo ngược kỹ thuật thở này nếu bạn đang sử dụng trọng lượng nhẹ.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'Machine seated row', 'Ngồi với ngực và bụng nằm chống trên đệm (đừng di chuyển cơ thể về phía sau). Nắm chặt các tay đòn bằng cách sử dụng tư thế cầm trung lập (lòng bàn tay hướng về nhau) và kéo về phía bụng đồng thời mở rộng ngực để co bó cơ lats. Lý thuyết cho rằng, bạn nên hít thở khi nâng trọng lượng lên và thở ra, mà không thở hết không khí, khi hạ nó xuống. Tuy nhiên, điều này khá không tự nhiên, vì hơi thở bình thường khó khăn khi ngực và bụng nằm chống trên máy. Ngoài ra, bạn sẽ cần giữ hơi cho một phần của động tác và không nên thở hết không khí tại bất kỳ điểm nào.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'Standing low cable row', 'Đứng đối diện máy cáp với cơ thể uốn cong ở hông khoảng 45º, đầu gối hơi uốn và lưng thẳng chắc chắn. Nắm chặt thanh kết nối với cáp thấp bằng cách giữ tay từ trên xuống với cánh tay hướng về phía trước và kéo về phía trên bụng, mở rộng ngực trong quá trình làm điều này. Hít thở khi bạn nâng trọng lượng lên và thở ra, mà không thở hết không khí, khi bạn hạ trọng lượng xuống.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'Behind-the-back-cable crossover', 'Đứng giữa hai dây cáp với đầu gối hơi uốn và một chân đưa về phía trước một chút (tùy chọn). Giữ cơ thể hơi uốn, mặc dù ít hơn so với Cable Crossover cho cơ ngực, và giữ cơ thể chắc chắn bằng cách sử dụng cơ bụng và cơ lưng dưới. Bắt đầu với cánh tay duỗi ra và khuỷu tay hơi uốn và đưa hai tay về phía nhau phía sau lưng. Giữ khuỷu tay ở vị trí trong suốt động tác. Nói chung, đây giống như Cable Crossover cho cơ ngực, nhưng đưa về phía sau lưng. Hít thở khi bạn mở cánh tay và thở ra khi bạn hoàn thành động tác.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Trung bình', 'Smith machine row', 'Tư thế giống như bài tập với tạ tự do (Bài tập 2). Đứng với cơ thể thẳng, uốn về phía trước với đầu gối hơi uốn nhưng khóa chặt. Nắm chặt thanh bằng tay cầm từ trên xuống (lòng bàn tay hướng xuống), tay hơi rộng hơn một chút so với rộng vai. Kéo thanh lên đến đỉnh bụng, giữ khuỷu tay mở ra. Hít thở khi bạn nâng trọng lượng lên và thở ra, mà không thở hết không khí, khi bạn hạ nó xuống.', 'nan', '8', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Nặng', 'Cable pull-down', 'Ngồi đối diện máy cáp với đùi trên đầu gối chạm vào giữa cản và lưng thẳng. Nắm chặt thanh với cách cầm từ trên xuống (nút xương hướng về sau), tay hơi rộng hơn một chút so với rộng vai. Kéo thanh xuống phía trên phần trên của ngực, đồng thời uốn lưng một chút để mở rộng ngực và nghiêng cơ thể về sau một chút. Khuỷu tay nên hạ xuống theo đứng đứng và song song với cánh cơ thể. Hít thở khi bạn nâng cánh tay lên và thở ra khi hạ chúng dọc theo thân dưới, mà không thở hết không khí. Kỹ thuật thở này có thể đảo ngược nếu bạn đang sử dụng trọng lượng nhẹ.', 'nan', '15', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Lưng (Back group)', 'Nặng', 'Standing cable pull-over', 'Đứng đối diện máy cáp và uốn cơ thể hơi về phía trước bằng cách căng cơ bụng. Nắm chặt thanh bằng tay cầm từ trên xuống (lòng bàn tay hướng xuống), tay cách nhau khoảng rộng vai. Duỗi cánh tay gần như đầy đủ, nhưng với khuỷu tay hơi uốn và góc cố định. Kéo thanh xuống tới eo hoặc phần trên đùi sử dụng cơ lats. Đừng uốn khuỷu tay. Mở rộng ngực và uốn lưng ở cuối động tác để có thêm đòn đẩy. Hít thở khi bạn nâng cánh tay lên và thở ra khi bạn kết thúc động tác hạ xuống. Kỹ thuật thở này có thể đảo ngược nếu bạn đang sử dụng trọng lượng nhẹ.', 'nan', '15', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nhẹ ', 'DUMBBELL LATERAL RAISE / SHOULDER FLY', 'Đứng với đôi chân mở rộng và đầu gối hơi uốn. Nắm chặt tạ với tư thế cầm trung lập (lòng bàn tay hướng về phía cơ thể), khuỷu tay hơi uốn và nằm ở hai bên cơ thể. Nâng cánh tay ra hai bên, đưa khuỷu tay lên đến mức tay và vai. Ở đỉnh động tác, lòng bàn tay của bạn nên hướng về phía sàn. Hạ tạ một cách kiểm soát. Hít thở trong khoảng một phần ba đầu động tác (hoặc trước khi bắt đầu động tác nâng trong các bộ nặng) và thở ra khi bạn hạ tạ.', 'nan', '15', '8', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nhẹ ', 'DUMBBELL FRONT RAISE', 'Đứng với đôi chân hơi mở rộng và cơ thể được giữ chặt. Nắm chặt tạ phía trước đùi với lòng bàn tay hướng về phía sau, hướng về cơ thể. Nâng tạ phía trước lên đến mức đầu, nên thực hiện ưa thích theo từng cánh tay. Giữ khuỷu tay ở vị trí suốt động tác và không di chuyển chúng về phía trong. Hít thở ở đầu động tác và thở ra khi bạn hoàn thành việc hạ trọng lượng.', 'nan', '15', '8', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nhẹ ', 'DUMBBELL REAR LATERAL RAISE', 'Đứng với đôi chân mở rộng và đầu gối hơi uốn. Giữ cơ thể chắc chắn và nghiêng về phía trước cho đến khi gần như ngang ngửa với mặt đất. Nắm chặt tạ với tư thế cầm trung lập, cánh tay treo xuống và khuỷu tay hướng về phía sau. Nâng cánh tay ra hai bên cho đến khi chúng đạt đến mức cao của cơ thể, duy trì vị trí của khuỷu tay. Quan trọng là giữ cho khuỷu tay đủ xa khỏi cơ thể để tránh sử dụng cơ lats quá mức. Hít thở khi bạn bắt đầu động tác nâng và thở ra khi bạn hạ tạ xuống, mà không làm hết hơi.', 'nan', '6', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nhẹ ', 'DUMBBELL LYING LATERAL RAISE', 'Nằm nghiêng một bên trên một bàn, giữ chặt bằng tay còn lại để giữ thăng bằng. Nắm chặt tạ đứng dọc lên bằng tư thế cầm trung lập (lòng bàn tay hướng về mặt đất) và hạ nó về phía trước chỉ xuống dưới mức cơ thể, sau đó đưa nó lên trở lại vị trí đứng dọc bằng cách co bóp cơ bả vai phía sau và các cơ lân cận. Ngón tay cái có thể hướng về phía sàn hoặc phía đầu của bạn khi bạn nắm chặt tạ. Hít thở khi bạn bắt đầu chuyển động hạ xuống và thở ra khi bạn đưa tạ lên.', 'nan', '6', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nhẹ ', 'LYING SIDE EXTERNAL ROTATION', 'Nằm nghiêng một bên trên một bàn hoặc thảm sàn và nắm chặt một tạ trong tay trên với tư thế cầm búa, tay hướng lên và khuỷu tay tạo góc vuông nằm trên bên cạnh cơ thể. Xoay cánh tay lên qua một cung 80º và sau đó hạ nó cho đến khi cánh tay song song với sàn nhà, hoặc hơi xa hơn một chút. Góc của tất cả các khớp duy trì nguyên vẹn suốt quá trình, chỉ có cổ vai quay, từ vị trí bên hông sang một vị trí phía trước. Thở tự nhiên hoặc hít thở khi bạn hạ tạ xuống và thở ra khi bạn đạt đến đỉnh chuyển động.', 'nan', '10', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nhẹ ', 'LYING NECK FLEXION', 'Nằm nghiêng về phía trên trên một chiếc bàn phẳng với đầu bạn vươn ra khỏi mép. Nếu bạn muốn, giữ chặt vào hai bên của bàn để duy trì thăng bằng. Cúi đầu và cổ, đưa chúng lên và về phía trước sao cho bạn nhìn thấy ngón chân. Hạ đầu xuống một chút so với vị trí nằm ngang. Bạn có thể đóng mắt nếu muốn. Thở tự nhiên, nhưng nếu có thể, hít thở khi bạn hạ đầu xuống.', 'nan', '10', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nhẹ ', 'LYING NECK ROTATION', 'Nằm nghiêng về một bên trên một chiếc bàn phẳng, được hỗ trợ bởi vai của bạn. Hãy nhìn về một hướng (xuống sàn) và sau đó quay đầu của bạn để nhìn về hướng ngược lại (lên trần nhà). Phạm vi chuyển động tối đa: khoảng 170º. Việc đóng mắt có thể làm cho bài tập trở nên thoải mái hơn. Thở tự nhiên.', 'nan', '10', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nhẹ ', 'SEATED MACHINE REAR LATERAL RAISE', 'Ngồi với ngực đặt trên gối đỡ trên máy. Nắm chặt tay cầm phía trước bạn, giữ khuỷu tay hơi uốn và xa xa từ cơ thể sao cho cánh tay của bạn nằm ngang với sàn nhà. Mở rộng cánh tay về phía sau giống như một cái ôm ngược. Đừng thay đổi góc của khuỷu tay. Thở vào khi bạn mở cánh tay và thở ra khi bạn đóng chúng, nhưng không hết khí hô hấp.', 'nan', '15', '8', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nhẹ ', 'MACHINE LATERAL RAISE', 'Ngồi trên ghế và nắm chặt tay cầm hai bên với khuỷu tay của bạn đang uốn (tùy thuộc vào thiết kế của máy). Đẩy bằng khuỷu tay để nâng cánh tay của bạn thẳng ra hai bên cho đến khi chúng song song với sàn nhà. Giữ khuỷu tay và phần còn lại của cơ thể ở trong tư thế. Hạ trọng lượng một cách kiểm soát. Hít thở vào khi bạn bắt đầu nâng và thở ra khi bạn kết thúc phần hạ.', 'nan', '10', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'Seated barbell military press', ' Ngồi trên một bàn được đặt ở một góc nghiêng nhẹ. Nắm chặt thanh với lòng bàn tay hướng về phía trước và đầu ngón tay hướng về phía sau (tư thế cầm từ trên xuống), tay cách nhau một chút so với rộng vai. Hạ thanh đến phía ngực, ở khoảng chiều cao của xương đòn và sau đó nâng lên cho đến khi cánh tay gần như đầy đủ duỗi ra. Cánh tay nên song song với cơ thể, di chuyển về phía hai bên khi bạn hạ trọng lượng. Hít thở khi bạn hạ thanh và thở ra khi bạn hoàn thành động tác nâng.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'SEATED DUMBBELL SHOULDER PRESS', 'Ngồi trên một bàn được đặt ở một góc nghiêng nhẹ hoặc phẳng. Nắm chặt tạ từ hai bên đầu với lòng bàn tay hướng về phía trước và đầu ngón tay hướng về phía sau (tư thế cầm từ trên xuống), và duỗi cánh tay gần như hoàn toàn mà không khoá chúng, đưa tay lại gần nhau một chút để duy trì sự cân bằng. Hạ tạ xuống đến mức tai một cách kiểm soát. Cánh tay nên song song với cơ thể, di chuyển về phía hai bên khi bạn hạ tạ. Hít thở khi bạn hạ tạ và thở ra khi bạn hoàn thành động tác nâng.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'FRONT DUMBBELL PRESS', 'Ngồi trên một bàn được đặt ở một góc nghiêng nhẹ và nắm chặt tạ phía trước đầu với tư thế cầm trung lập và ngón cái hướng về phía trong. Đẩy lên mà không thay đổi hướng của khuỷu tay (mà luôn hướng về phía trước). Đưa tạ xuống đến mức xương đòn và nâng chúng lên cho đến khi cánh tay gần như duỗi ra hoàn toàn. Hít thở khi bạn hạ tạ và thở ra khi bạn hoàn thành động tác nâng.', 'nan', '15', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'BARBELL UPRIGHT ROW', 'Đứng với đôi chân hơi mở rộng, cơ thể thẳng và giữ chặt. Nắm chặt thanh tạ đối với đùi với tư thế cầm trung lập (lòng bàn tay hướng về chân) với đôi tay cách nhau một chút ít hơn rộng vai. Kéo thanh tạ lên đến cổ, giữ nó gần cơ thể suốt quá trình chuyển động. Khuỷu tay nên nâng lên và duy trì ở vị trí cao hơn so với tay. Hít thở khi bạn bắt đầu nâng thanh tạ lên và thở ra khi bạn hạ nó xuống.', 'nan', '15', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'DUMBBELL SHRUG', 'Đứng với đôi chân hơi mở rộng, cơ thể thẳng và giữ chặt. Nắm chặt tạ với tư thế cầm trung lập ở hai bên cơ thể. Nâng cao vai càng cao càng tốt và cố gắng tạm nghỉ một khoảnh khắc ở điểm cao nhất. Cánh tay chỉ giữ trọng lượng, mặc dù sức đề kháng là không đổi. Hít thở ngay trước khi nâng vai lên và thở ra khi hạ chúng xuống.', 'nan', '15', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'BENT-OVER FRONT RAISE', 'Đặt cơ thể của bạn theo cùng cách như khi thực hiện Bài tập Cơ Lưng với Tạ Gập (Lưng, Bài 4), nghỉ một tay và một đầu gối lên bàn phẳng để hỗ trợ cơ thể. Nắm chặt tạ với tư thế cầm trung lập với tay còn lại. Chân còn lại của bạn nên đặt trên sàn nhẹ nhàng phía sau cơ thể (tùy thuộc vào chiều cao của bàn) và bạn nên giữ chân duỗi ra một chút ở đầu gối. Hãy uốn người về phía trước từ hông để cơ thể nằm ngang và thẳng hàng. Nâng tay lên phía trước từ vai đến mức ngang với cơ thể, giữ cho khuỷu tay ở vị trí cố định. Hít thở khi bạn nâng tạ lên và thở ra khi bạn hạ nó xuống.', 'nan', '12', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'LYING LATERAL NECK FLEXION', 'Nằm nghiêng bên một chiếc bàn phẳng với vai làm chỗ chống. Đưa đầu nhẹ nhàng xuống vai và sau đó đưa lên lại (phạm vi chuyển động tối đa: 90º). Có thể thoải mái hơn nếu bạn làm điều này với mắt đóng. Thở tự nhiên.', 'nan', '15', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'MACHINE SHOULDER PRESS', 'Ngồi trên ghế máy, được đặt đứng hoặc có độ nghiêng nhẹ, và nắm chặt tay cầm với kiểu cầm trái tim lên (bàn tay hướng lên với đốt ngón cái về phía sau) và tay hơi rộng hơn so với rộng vai một chút. Hạ trọng lượng cho đến khi tay bạn ở độ cao của đầu và nâng lên cho đến khi cánh tay của bạn gần như duỗi ra hoàn toàn. Cánh tay của bạn nên song song với cơ thể, di chuyển về phía hai bên khi bạn hạ trọng lượng. Hít thở vào khi bạn hạ thanh trượt và thở ra khi bạn hoàn thành động tác.', 'nan', '15', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'SMITH MACHINE MILITARY PRESS', 'Ngồi trên bàn được đặt ở một độ nghiêng nhẹ dưới thanh Smith machine. Nắm chặt thanh với kiểu cầm trái tim lên (đốt ngón cái hướng về phía sau) với hai tay cách nhau một chút hơn so với rộng vai. Hạ trọng lượng cho đến khi tay bạn ở độ cao của đầu và nâng lên cho đến khi cánh tay của bạn gần như duỗi ra hoàn toàn. Cánh tay của bạn nên nằm song song với cơ thể, di chuyển về phía hai bên khi bạn hạ trọng lượng. Nếu đầu của bạn cản trở khi bạn hạ thanh trượt, bạn có thể quay nó về một bên. Hít thở vào khi bạn hạ thanh trượt và thở ra khi bạn hoàn thành động tác.', 'nan', '15', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Trung bình', 'LYING NECK EXTENSION', 'Nằm với ngực và bụng chạm vào một chiếc bàn phẳng, xương ngực nghỉ trên mép và đầu bạn nằm qua mép. Bạn có thể ôm chặt bàn để duy trì thăng bằng hoặc đặt tay sau lưng. Đưa đầu lên như đang nhìn lên và từ từ hạ nó xuống trong một chuyển động kiểm soát cho đến khi bạn nhìn thấy phía dưới bàn. Khi bạn quen với động tác, bạn có thể thực hiện bài tập này mà không cần mở mắt. Thở tự nhiên.', 'nan', '15', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nặng', 'ONE-ARM DUMBBELL LATERAL RAISE', 'Đứng với đôi chân mở rộng và đầu gối hơi uốn. Nắm chặt tạ với tư thế cầm trung lập (lòng bàn tay hướng về phía trước) với khuỷu tay hơi uốn và nằm bên cạnh cơ thể. Đặt tay còn lại lên hông hoặc nắm chặt vào một thứ gì đó để hỗ trợ. Nâng cánh tay ra xa khỏi cơ thể lên khoảng 90º mà không thay đổi vị trí của khuỷu tay hoặc phần còn lại của cơ thể. Khuỷu tay nên lên đến cùng mức với tay và vai, và tạ nên song song với sàn. Bạn có thể bắt đầu động tác với tay phía trước cơ thể, điều này mang lại một lượng động lượng nhỏ. (Điều này không strict lắm, nhưng cho phép bạn sử dụng một trọng lượng nặng hơn.) Hạ tạ một cách kiểm soát. Hít thở khi bạn bắt đầu động tác nâng và thở ra khi bạn kết thúc động tác hạ.', 'nan', '10', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nặng', 'HANDSTAND PUSH-UPS', 'Thực hiện động tác đứng chổ với tay được đặt vào tường, giữ cơ thể thẳng. Gập khuỷu tay để đưa đầu xuống sàn và sau đó đẩy lên lại. Hít thở ở đầu chuyển động và thở ra khi bạn đẩy lên lại.', 'nan', '8', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nặng', 'SHOULDER DIP', 'Nghỉ cả hai cánh tay và khuỷu tay trên thanh đôi của bài tập parallel dip, và để cơ thể treo tự do. Sau đó, đẩy cơ thể lên bằng cách đẩy thẳng xuống bằng vai, mà không sử dụng khuỷu tay. Hít thở khi bạn hạ cơ thể xuống và thở ra khi bạn đẩy nó lên.', 'nan', '10', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cổ và vai (Neck & Shoulders 3 Group)', 'Nặng', 'CABLE UPRIGHT ROW', 'Đứng với đôi chân hơi rộng, thân hình thẳng và giữ chặt. Nắm thanh đàn hồi ở phía trước đùi bạn với tay cầm với cách nhau một chút so với rộng vai. Kéo thanh lên đến cổ, giữ nó gần cơ thể suốt quá trình chuyển động. Khuỷu tay nên nâng lên và duy trì ở vị trí cao hơn so với tay. Thở vào khi bạn bắt đầu nâng thanh và thở ra khi hạ nó.', 'nan', '10', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay (Biceps group)', 'Nhẹ', 'DUMBBELL CONCENTRATION CURL', 'Ngồi trên băng và nghiêng người về phía trước (đảm bảo đứng thẳng), đặt trọng lượng của bạn với một tay trên đùi. Nắm chặt tay có tạ theo kiểu underhand grip (bàn tay hướng lên trên), đặt khuỷu tay (phần dưới của cơ tam đầu) vào bên trong đùi. Hạ tạ cho đến khi cánh tay của bạn gần như duỗi ra hoàn toàn và sau đó nâng lên lại, giữ cho động tác luôn chặt chẽ. Hít thở vào trong khoảng một phần ba của chuyển động xuống và thở ra khi kết thúc lời nâng.', 'nan', '10', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay (Biceps group)', 'Nhẹ', 'LYING DUMBBELL CURL', 'Nằm ngửa trên một chiếc ghế dài, giữ một tạ nhẹ trong một tay. Hạ tạ xuống càng sâu tùy thuộc vào độ linh hoạt của bạn, sau đó đưa nó lên bằng cách uốn khuỷu tay. Thở tự nhiên hoặc hít thở vào khi hạ tạ và thở ra khi đạt đến đỉnh điểm của chuyển động.', 'nan', '15', '8', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay (Biceps group)', 'Nhẹ', 'BICEPS PULL-UPS', 'Dựa vào một thanh ngang, giữ lấy bằng cách áp dụng cổ tay (bàn tay hướng về đầu) và đưa tay ra phía trước một chút so với bề rộng vai. Kéo phần trên của ngực lên đến mức thanh ngang, đồng thời làm cong cơ thể về phía sau một chút. Để chân thả tự do xuống hoặc chéo chúng. Bạn cũng có thể uốn đầu gối nếu muốn. Khuỷu tay của bạn nên hướng về phía trước suốt quá trình chuyển động. Hít thở vào trước khi kéo lên, giữ hơi thở trong suốt chuyển động và thở ra khi hạ xuống (phần cuối của chuyển động xuống).', 'nan', '10', '9', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay (Biceps group)', 'Trung bình', 'BARBELL CURL', 'Đứng chân rộng để giữ thăng bằng. Giữ thân trên, vai và cổ tay ổn định. Nắm chặt thanh tạ với kiểu cầm ngược (đầu bàn tay hướng lên trên), tay cách nhau một chút hơn so với bề rộng vai. Nâng lên một cách kiểm soát cho đến khi cánh tay được uốn cong càng xa càng tốt (khoảng 145º, tuy nhiên có thể ít hơn nếu cơ bắp cơ bắp cánh tay lớn). Hít thở vào ngay trước khi nâng và thở ra khi hạ thanh tạ xuống.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay (Biceps group)', 'Trung bình', 'DUMBBELL CURL', 'Đứng chân rộng để giữ thăng bằng, hoặc ngồi, giữ thân trên ổn định. Nắm chặt tạ ở tư thế cầm giữ trung tính (đầu bàn tay hướng vào trong) hai bên cơ thể và nâng chúng trong một cử động kiểm soát. Đồng thời, xoay bàn tay vào trong (quay mở) để lòng bàn tay hướng về vai khi ở điểm cao nhất. Xoay cổ tay trở lại tư thế trung tính khi hạ tạ xuống. Hít thở vào ngay trước khi nâng và thở ra khi hạ tạ.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay (Biceps group)', 'Trung bình', 'MACHINE CURL', 'Ngồi với phần ba dưới của cánh tay nghỉ trên đệm, với phần nách ở mép và khuỷu tay trong đường trục của máy. Nắm chặt tay cầm bằng lòng bàn tay hướng lên (làm nghiêng về phía trên), giữ khuỷu tay của bạn ở trong tư thế uốn. Hạ tạ cho đến khi cánh tay của bạn gần như duỗi thẳng (để tránh làm tổn thương khớp khuỷu tay) và đưa nó lên lại đến điểm bắt đầu. Hít thở vào trong một phần ba đầu tiên của chuyển động hạ và thở ra ở cuối của động tác nâng lên.', 'nan', '10', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay (Biceps group)', 'Trung bình', 'CABLE CURL', 'Đứng trước một dây cáp thấp với chân mở rộng để giữ thăng bằng và giữ thân trên chặt chẽ. Nắm chặt thanh cầm ở phía trước của đùi bằng cách xoay lòng bàn tay lên (palms up) với tay cách nhau hơn một chút so với rộng vai. Nâng lên trong một chuyển động kiểm soát cho đến khi cánh tay uốn cong càng xa càng tốt (khoảng 145º). Hít thở vào ngay trước khi nâng và thở ra khi giảm trọng lượng.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay (Biceps group)', 'Nặng', 'BARBELL PREACHER CURL / SCOTT CURL', 'Ngồi với cánh tay sau triceps nghỉ trên đệm của bàn làm việc (đặt ở góc nghiêng) và nách ở phía trên cạnh trên. Nắm chặt thanh tạ với kiểu cầm giữ trung tính (đầu bàn tay hướng lên), giữ khuỷu tay hơi cong. Hạ thanh tạ cho đến khi cánh tay gần như duỗi ra hoàn toàn (để tránh chấn thương khớp khuỷu tay) và nâng nó lên lại đến điểm xuất phát. Hít thở vào trong khoảng một phần ba của chuyển động xuống và thở ra ở cuối độ nâng.', 'nan', '15', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay (Biceps group)', 'Nặng', 'PRONE CURL ON INCLINE BENCH', 'Nằm bẹp xuống trên bàn nghiêng ở góc 45º. Nắm chặt một chiếc tạ bằng cách giữ nó bằng cổ tay hoặc bằng cách sử dụng thanh tạ và thực hiện động tác nâng cánh mà không đưa ngực ra khỏi bàn. Hít thở vào khi bắt đầu đưa tạ lên và thở ra trong quá trình hạ tạ xuống.', 'nan', '15', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Nhẹ ', 'LYING FRENCH PRESS / BARBELL LYING TRI', 'Nằm ngửa trên bàn, đặt chân lên sàn nếu bàn thấp hoặc lên một bậc. Nắm thanh tạ bằng cách đặt tay trên nó với lòng bàn tay hướng lên trên và cánh tay thẳng. Hạ thanh tạ bằng cách uốn khuỷu tay, nhưng không thay đổi vị trí của chúng. Duỗi cánh tay để nâng thanh tạ lên và dừng ngay trước khi chúng thẳng hoàn toàn, giữ cho căng trước. (Bạn cũng có thể đặt khuỷu tay lên một chút.) Hít thở vào trong giai đoạn đầu của chuyển động hạ và thở ra khi bạn đạt đến đỉnh.', 'nan', '15', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Nhẹ ', 'DUMBBELL FRENCH PRESS / DUMBBELL LYING', 'Nằm ngửa trên bàn, đặt chân lên sàn nếu bàn thấp hoặc lên một bậc. Giữ hai tạ ở tay với kiểu cầm trung lập, đặt trên đỉnh mắt và làm thẳng khuỷu tay. Hạ tạ về hai bên đầu mà không thay đổi vị trí của khuỷu tay. Khi đạt đến đáy chuyển động, duỗi cánh tay để nâng tạ lên đến điểm xuất phát. Thở vào khi hạ tạ và thở ra khi nâng chúng lên.', 'nan', '15', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Nhẹ ', 'TRICEPS PUSH-UP', 'Nằm ở tư thế úp mặt xuống trên sàn với chân gần nhau và tay rộng bằng vai. Bắt đầu với cánh tay duỗi thẳng, hít thở và hạ cơ thể bằng cách uốn khuỷu tay, giữ chúng gần thân dưới. Đừng uốn cơ hông. Thở ra sau khi duỗi thẳng cánh tay để đẩy cơ thể lên trở lại.', 'nan', '10', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Nhẹ ', 'NARROW-GRIP BENCH PRESS', 'Nằm phẳng (mặt lên trên) với bàn hỗ trợ đầu và lưng của bạn và chân chắc chắn trên mặt đất. Với thanh tạ nằm ngay trên mắt của bạn, nắm nó bằng cách sử dụng một kiểu cầm đối xứng (palms hướng lên), tay cách nhau một chút so với chiều rộng vai, nên giữ ngón cái dưới thanh tạ nếu có thể. Hít thở vào trước khi đưa thanh tạ xuống và đưa nó vào đường thẳng với ngực của bạn. Hạ khuỷu tay ra xa khỏi thân trên đến khi thanh tạ chỉ còn trên giữa ngực và sau đó đưa nó lên theo đường thẳng. Thở vào khi hạ thanh tạ và thở ra khi hoàn thành động tác.', 'nan', '10', '10', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Trung bình', 'DUMBBELL KICK-BACK', 'Ở vị trí tựa vào một tay và một chân, nằm trên một chiếc bàn phẳng. Nắm lấy chiếc tạ bằng cách giữ nó ở tư thế tự nhiên với tay còn lại, chân kia duỗi ra chéo và chạm đất. Giữ thân trên ngang và thẳng hàng. Bắt đầu với khuỷu tay uốn góc 90º, nâng chiếc tạ lên và về phía sau cho đến khi cơ triceps gần như duỗi thẳng. Giữ khuỷu tay gần thân mà không thay đổi vị trí. Hít thở vào ngay trước khi nâng tạ lên và thở ra khi hạ xuống.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Trung bình', 'PARALLEL BAR DIP', 'Nắm chặt thanh đôi với tay ở tư thế hẹp và trong tư thế trung lập, giữ cho cơ thể thẳng và đầu gối hơi cong. Hít thở và hạ cơ thể của bạn mà không làm cơ thể bạn nghiêng về phía trước để tránh sử dụng cơ ngực. Bạn nên cảm thấy như bạn đang "duỗi thẳng" cánh tay thay vì "đẩy." Để đạt được điều này, hãy đảm bảo rằng khuỷu tay của bạn không di chuyển quá xa khỏi cơ thể. Đẩy lên khi bạn đạt đến điểm thấp nhất của chuyển động và thở ra ở đỉnh.', 'nan', '10', '12', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Trung bình', 'DUMBBELL OVERHEAD TRICEPS EXTENSION', 'Đứng hoặc ngồi nghiêng lưng vào một bảng tựa không đến đầu của bạn và co cơ bụng để giữ cho thân trên của bạn giữ ổn định. Nắm thanh tạ bằng phần trong của đĩa với ngón cái ở phía bên kia của thanh tạ và ngón tay nắm chặt trọng lượng. Thẳng khuỷu tay của bạn lên phía trên đầu gần như tới giới hạn của chúng và mà không mở rộng chúng, hạ thanh tạ về phía sau đầu của bạn. Thở vào khi hạ thanh tạ và thở ra khi đưa nó lên.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Trung bình', 'CABLE PUSH-DOWN', 'Đứng trước một dây cáp cao và nắm thanh tạ bằng cách nắm chặt từ trên (palms hướng xuống). Bạn có thể đặt ngón cái của mình ở trên hoặc dưới thanh tạ và tay bạn nên cách nhau một khoảng bằng hoặc nhỏ hơn chiều rộng vai. Giữ đôi chân cách xa và song song, đặt một chân về phía trước một chút để chịu trọng lượng của cơ thể và cánh tay. Giữ thân trên của bạn ổn định bằng cách căng cơ bụng. Kéo cánh tay của bạn ra để hạ thanh tạ mà không di chuyển khuỷu tay, mà bạn nên giữ gần cạnh cơ thể. Khi cánh tay được duỗi hết, đưa chúng lên lại trong một cử động kiểm soát. Thở vào khi gập khuỷu tay và thở ra khi cánh tay của bạn được duỗi ra.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Trung bình', 'ROPE PUSH-DOWN', 'Đứng trước một dây cáp cao giữ một sợi dây dày trong tư thế nắm bình thường với tay ở một khoảng nhỏ hơn chiều rộng vai. Giữ đôi chân cách xa, đặt một chân về phía trước một chút để chịu trọng lượng của cơ thể và cánh tay. Sử dụng cơ bụng của bạn để giữ cho thân trên ổn định và duỗi khuỷu tay của bạn xuống mà không thay đổi vị trí của chúng so với cơ thể của bạn. Thở vào khi gập khuỷu tay và thở ra khi cánh tay của bạn được duỗi ra.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Trung bình', 'MACHINE EXTENSIONS', 'Bài tập này thực hiện khi ngồi trên một bàn tương tự như bàn cầm tay (Biceps, Bài tập 3). Nghỉ cánh tay trên gối với khuỷu tay tự do (tuỳ thuộc vào thiết kế) và thẳng hàng với trục của máy. Nắm giữ tay ở vị trí trung lập (hoặc bất kỳ kiểu nắm nào mà máy cho phép) và duỗi cánh tay gần như hoàn toàn. Thở vào khi gập khuỷu tay và thở ra khi bạn hoàn thành phần duỗi cánh tay.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Trung bình', 'HORIZONTAL / MACHINE PRESS', 'Ngồi tại máy ép ngực/cánh tay ngang (máy của bạn phải có chế độ cánh tay), và nắm giữ tay cầm bằng cách đóng kín tay (ngón cái quấn quanh thanh tay) với khuỷu tay gần cơ thể. Thực hiện động tác ép/duỗi cánh tay (không bao giờ khóa khuỷu tay), đảm bảo rằng cơ triceps là nguyên nhân chính gây ra nỗ lực, không phải ngực và vai. Thở vào trước khi gập khuỷu tay và thở ra khi bạn kết thúc phần duỗi.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Trung bình', 'BEHIND-THE-BACK CABLE EXTENSION', 'Đứng với lưng hướng về một dây đàn hồi đặt ở độ cao và nắm chặt tay cầm hoặc bằng cách nắm chặt từ trên xuống hoặc từ dưới lên, tương tự như Bài tập Bench Dip. Giữ chân hơi rộng và song song. Giữ phần thân cố định. Duỗi cánh tay xuống và phía sau sau đó nâng dây lên một cách kiểm soát. Hít thở vào cuối phần di chuyển lên và thở ra khi bạn hoàn thành phần mở rộng xuống.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bắp tay sau (Triceps group)', 'Nặng', 'ASSISTED DIP', 'Đây giống như Bài tập Bar Dip , nhưng lần này với đầu gối hoặc chân nằm trên một nền tảng (tùy thuộc vào thiết kế của máy). Nắm chặt thanh song song với một khoảng cách nhỏ, giữ cho phần thân trên thẳng. Hít thở vào và hạ cơ thể của bạn mà không cúi người về phía trước để tránh sử dụng cơ ngực. Đẩy cơ lên khi bạn đạt đến điểm thấp nhất của động tác và thở ra.', 'nan', '10', '15', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cẳng tay (Foream Group)', 'Nhẹ', 'STANDING BARBELL WRIST CURL', 'Đứng với đôi chân cách xa nhau để giữ thăng bằng và giữ thân trên cơ thể cố định. Nắm thanh tạ đằng sau lưng với kiểu nắm từ phía sau (nắm với đầu ngón tay hướng về phía cơ thể), tay cách nhau khoảng chiều rộng vai. Bắt đầu với thanh tạ gần như nằm ở đầu các ngón tay duỗi ra. Đóng các ngón tay lại và uốn cổ tay lên hướng cổ tay (75-80º). Mở bàn tay và để thanh tạ cuộn trở lại xuống điểm khởi đầu một cách kiểm soát. Hô hấp không phải là một yếu tố quan trọng trong hầu hết các bài tập cơ cánh tay. Hít thở một cách tự nhiên.', 'nan', '10', '6', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cẳng tay (Foream Group)', 'Nhẹ', 'SEATED REVERSE BARBELL WRIST CURL', 'Ngồi trên một bàn với tư thế giống như trong bài tập Wrist Curl, với cánh tay nằm ngang trên đùi hoặc nghiêng xuống một chút, cổ tay ở cuối đầu gối và bàn tay tự do. Nắm thanh tạ bằng cách nắm từ phía trên (cạm tay hướng xuống) và uốn cổ tay lên trên (uốn tối đa 70°-80º). Hạ thanh tạ trở lại vị trí ban đầu một cách kiểm soát. Bạn cũng có thể thực hiện biến thể này khi quỳ với cánh tay nằm ngang qua chiều rộng của một chiếc bàn và bàn tay tự do. Hít thở một cách tự nhiên.', 'nan', '10', '6', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cẳng tay (Foream Group)', 'Nhẹ', 'REVERSE BARBELL CURL', 'Đứng ở tư thế giống như trong bài tập Barbell Curl , với đôi chân tách ra một chút để cân bằng và cơ thể giữ vững. Nắm thanh tạ phía trước cơ thể với kiểu cầm từ trên (lòng bàn tay hướng xuống) và tay cách nhau khoảng chiều rộng vai. Hít thở vào và nâng thanh tạ lên một cách kiểm soát cho đến khi khuỷu tay uốn càng xa càng tốt. Thở ra khi hạ thanh tạ. Bạn cũng có thể thở tự nhiên, miễn là trọng lượng sử dụng không quá nặng.', 'nan', '10', '6', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cẳng tay (Foream Group)', 'Trung bình', 'WRIST ROLLER', 'Đứng với cánh tay duỗi thẳng và nghỉ trên một nền đệm có sẵn, tay hoàn toàn tự do. Nắm cuộn ở dạng kiểu cầm từ trên (lòng bàn tay hướng xuống). Một dây hoặc cáp kéo xuống sàn được buộc với cuộn ở một đầu, với một tạ ở đầu kia. Cuộn dây/cáp lên để nâng tạ bằng cách hạ cổ tay xuống, xen kẽ giữa hai bàn tay. Khi tạ đạt đến đỉnh, hạ nó xuống lại bằng cách thực hiện chuyển động ngược lại (tăng căng). Thở tự nhiên.', 'nan', '10', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Cẳng tay (Foream Group)', 'Nặng', 'NEUTRAL DUMBBELL WRIST CURL', 'Ngồi ở vị trí giống như khi thực hiện Seated Barbell Wrist Curl. Nắm chặt vào đầu "nửa tạ" (tạ chỉ có một trọng lượng ở một đầu của cần trung tâm). Bắt đầu với đầu có trọng lượng hướng lên trên và hạ nó chậm chạp về phía trước cho đến khi nó gần như nằm ngang (khoảng 30º, tùy thuộc vào sự linh hoạt). Hãy uốn cổ tay để nâng trọng lượng lên đến vị trí bắt đầu thẳng đứng (0º, mặc dù bạn có thể đi thêm khoảng 15º nếu cánh tay của bạn nghiêng xuống). Thở tự nhiên.', 'nan', '10', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nhẹ', 'STANDING CALF RAISE', 'Đứng với đôi chân cách nhau, khoảng bằng chiều rộng hông, và bàn chân đặt trên mép của một bậc thang. Hạ thân dưới một cách kiểm soát. Giữ đầu gối hơi cong để bảo vệ chúng. Bạn có thể thở tự nhiên, mặc dù việc hít thở khi hạ cơ thể và thở ra ở đỉnh động tác giúp duy trì nhịp.', 'nan', '1', '8', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nhẹ', 'BARBELL SEATED CALF RAISE', 'Ngồi với đầu gối tạo thành góc 90º và phần trước của bàn chân nằm trên một bậc thấp, để bắp chân không chạm sàn. Đặt một thanh có đệm với trọng lượng mong muốn qua đùi, chính giữa phía trên đầu gối, và nâng gót chân cao nhất có thể. Giữ vị trí này trong một khoảnh khắc và sau đó hạ gót chân xuống mà không để chúng chạm sàn. Hít thở theo tự nhiên.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nhẹ', 'MACHINE LYING LEG CURL', 'Nằm bụng xuống trên một chiếc bàn (nên hơi uốn ở hông) và nắm chặt vào tay cầm hoặc mép bàn để ổn định cơ thể. Đặt chân dưới các con lăn ở đầu gối Achilles với đầu ngón chân hướng xuống sàn nhà, đầu gối nằm trên bàn và thẳng hàng với trục của máy. Bắt đầu với đôi chân gần như thẳng, nâng trọng lượng bằng cách uốn đầu gối càng xa càng tốt (khoảng 120º) trong một động tác kiểm soát. Không bao giờ để trọng lượng xuống đến khi chân được duỗi đầy đủ. Thở vào khi bắt đầu hạ trọng lượng và thở ra khi hoàn thành động tác uốn gối.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nhẹ', 'SEATED CALF RAISE', 'Ngồi với đầu gối uốn thành góc vuông (90º) và bó chân đặt trên bảng đỡ chân, giữ cho gót chân tự do. Đặt đệm trên phần dưới của cơ đùi và nâng gót chân cao nhất có thể. Giữ vị trí trong một khoảnh khắc và hạ gót chân. Thở tự nhiên.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nhẹ', 'SEATED HIP ADDUCTION', 'Ngồi trong máy cơ bắp đùi bên trong với các đệm nằm ở phía trong của mắt cá chân hoặc đầu gối (tùy thuộc vào thiết kế). Mở chân ra càng rộng càng tốt và sau đó đóng chúng lại (adduction). Thở tự nhiên, hoặc nếu bạn sử dụng trọng lượng nặng, thì hít thở khi bạn mở chân và thở ra khi bạn kết thúc đóng chúng.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nhẹ', 'SEATED HIP ABDUCTION', 'Ngồi trong máy "abductor" (tên gọi không chính xác vì không có cơ bắp "abductor" nào cụ thể ở khu vực này, chỉ có các cơ bắp thực hiện chức năng này) với các đệm nằm ở phía ngoài của mắt cá chân hoặc đầu gối (tùy thuộc vào thiết kế). Mở chân ra càng rộng càng tốt và sau đó đóng chúng lại. Thở tự nhiên hoặc, nếu bạn sử dụng trọng lượng nặng, hít thở khi bạn mở chân và thở ra khi bạn kết thúc đóng chúng.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nhẹ', 'CABLE CALF RAISE', 'Tương tự như bài tập với tạ tự do, đứng với bóng chân của bạn trên mép của một bề mặt nâng và chân cách nhau khoảng hip. Nắm lấy tay cầm được gắn vào dây cáp phía sau lưng để giúp duy trì thăng bằng. Đưa chân xuống để nâng gót chân (và do đó là toàn bộ cơ thể) càng cao càng tốt. Hạ gót chân xuống một cách kiểm soát. Giữ đầu gối một chút uốn để bảo vệ chúng. Hít thở theo tự nhiên.', 'nan', '10', '10', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Trung bình', 'STANDING HIP ABDUCTION', 'Đứng và nắm chặt một đối tượng cố định bằng một tay để giữ thăng bằng. Nâng một chân lên về phía bên (abduction) từ vị trí thẳng đứng đến điểm cao nhất bạn có thể đạt được mà không làm di chuyển cơ thể. Hạ chân xuống một chút phía trên điểm khởi đầu. Thở vào khi bạn nâng chân lên và thở ra khi bạn hạ chân xuống.', 'nan', '20', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Trung bình', 'STEP-UP', 'Đứng trước bậc thang, giữ một tạ bằng một tay (sử dụng tay kia để giữ thăng bằng). Đặt một chân lên bậc thang, nơi nên hơi thấp hơn độ cao đầu gối, và đẩy cơ lên bậc thang chỉ bằng sức mạnh của chân đang đẩy lên. Giữ cho lưng thẳng. Đổi chân để bước xuống và lặp lại chuyển động. Hít thở vào trước khi bước lên và thở ra khi bước xuống. Bạn cũng có thể thở tự nhiên.', 'nan', '20', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Trung bình', 'LUNGE', 'Đứng giữ thanh tạ bằng cách nắm lấy nó từ trên (Squat), đặt nó trên cơ vai và cơ đùi. Bước một bước dài về phía trước và để trọng lượng của bạn chuyển sang chân phía trước, đồng thời hạ đầu gối chân sau xuống gần mặt đất. Giữ cho lưng thẳng và tập trung vào việc giữ thăng bằng. Chân phía trước nên thẳng hàng với đầu gối của bạn theo chiều dọc. Đứng thẳng trở lại bằng cách đẩy chân phía trước. Hít thở vào khi bước về phía trước và thở ra khi hoàn thành bước về phía sau.', 'nan', '10', '12', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Trung bình', '45º CALF PRESS', 'Đứng trong tư thế tương tự như khi thực hiện Leg Press , nhưng chỉ có đầu gối chân đặt lên mặt đế chân. Giữ đầu gối hơi uốn để bảo vệ chúng. Duy trì chân ở dạng chân, cố gắng đảm bảo một phạm vi hoàn chỉnh của động tác. Khi bạn hoàn thành, uốn đầu gối để gỡ trọng lượng trước khi bỏ chân ra khỏi máy. Thở tự nhiên.', 'nan', '15', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Trung bình', 'CABLE LYING HIP ABDUCTION', 'Nằm ngửa với mặt nghiêng về phía dây cáp. Đưa chân thẳng lên trời và tạo trở kháng khi hạ chân ra xa cơ thể (abduction). Hít thở một cách tự nhiên hoặc, nếu bạn sử dụng nhiều trọng lượng (không phổ biến), thì hít thở khi bạn bắt đầu nâng và thở ra khi bạn hoàn thành.', 'nan', '10', '12', '1.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Trung bình', 'KICK-BACK', 'Quỳ bốn chân (quỳ đầu gối và chống cơ thể bằng tay hoặc khuỷu tay), nâng một đầu gối và đẩy chân lên phía sau. Giữ cho cử động được kiểm soát, nâng chân lên cao hơn mặt phẳng ngang nhưng không làm cong quá mức phần lưng. Nếu bạn chống cơ thể bằng khuỷu tay thay vì bằng tay, điều này sẽ dễ hơn. Khi bạn mở rộng hông, bạn cũng có thể mở rộng chân để chuyển một số công việc sang cơ bắp đùi sau. Thở tự nhiên hoặc hít thở khi đẩy chân lên.', 'nan', '15', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nặng', 'SISSY SQUAT', 'Đứng với đôi chân cách nhau rộng bằng hông. Nắm chặt một đối tượng để hỗ trợ bằng một tay và đặt tay còn lại lên hông hoặc, nếu bạn muốn, chống ngang ngực cầm một tạ. Hạ toàn bộ cơ thể của bạn với một sự uốn đầu gối sâu trong khi nghiêng cơ thể về phía sau. Đừng uốn người ở eo. Gót chân của bạn sẽ nâng lên. Trong phần di chuyển trở lại, đưa cơ thể của bạn về phía trước và lên, tập trung vào việc sử dụng cơ đùi trước. Hít vào khi bạn đi xuống và thở ra khi bạn hoàn tất phần di chuyển lên.', 'nan', '10', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nặng', 'SLED SQUAT', 'Đứng hướng về phía trước với đôi chân hơi nghiêng ra ngoài và cách nhau một chút hơn chiều rộng của hông. Nghỉ đầu gối trên cơ trapezius và deltoid. Uốn đầu gối ra ngoài (đồng trục với đôi chân) cho đến khi đùi gần như song song với sàn nhà. Giữ cho lưng thẳng, tuy nhiên, bạn có thể cho phép phần thấp của lưng có chút cung. Đầu gối của bạn nên gần như ở phía trên của đôi chân ở đáy phần di chuyển. Đừng nâng gót chân lên. Để tránh điều này, đặt đôi chân của bạn hơi về phía trước nếu cần. Hít vào trước khi bạn hạ cơ thể xuống, sau đó giữ hơi và thở ra khi bạn đạt đến đỉnh phần di chuyển. Thở sâu và lặp lại.', 'nan', '10', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nặng', 'LEG PRESS', 'Nằm trên máy đẩy chân nghiêng với toàn bộ lưng và hông được hỗ trợ. Đặt đôi chân lên tấm chân với khoảng cách hơi rộng hơn chiều rộng của hông, và ngón chân hơi nghiêng ra ngoài. Hạ bảng chân cho đến khi đùi gần như tiếp cận với bụng nhưng không làm nâng cao mông khỏi bàn. Đẩy tấm chân lên với một cú đẩy kiểm soát nhưng năng động cho đến khi đôi chân gần như được duỗi hết. Thở vào trong phần di chuyển xuống ở phần một/ ba đầu tiên và thở ra khi hoàn thành phần đẩy.', 'nan', '10', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nặng', 'MACHINE LEG EXTENSION', 'Ngồi trên bàn đặc biệt thiết kế với phần phía trước của mắt cá chân dưới tay đòn đệm. Phần sau của đầu gối nên nằm ở mép của bàn và khớp gối nên nằm trong trục của máy. Nâng trọng lượng từ khoảng 90º đến khi chân gần như duỗi hết, sau đó hạ nó một cách kiểm soát. Thông thường, không khuyến khích việc uốn đầu gối quá mức nếu tải trọng quá lớn hoặc trong quá trình tái tạo. Thở vào khi hạ trọng lượng và thở ra khi hoàn thành động tác nâng.', 'nan', '10', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nặng', 'STANDING HIP EXTENSION', 'Đứng với một bên đối diện với máy đa dây, trục của máy phải ở đường thẳng với hông của bạn, nên hông hơi uốn cong. Nắm chặt máy bằng tay và giữ cơ thể của bạn ổn định. Tùy thuộc vào thiết kế của máy, đặt cuộn lông gà có đệm qua cơ chân dưới hoặc phía sau đầu gối của bạn (nếu có thể). Giữ đầu gối hơi uốn. Nâng chân của bạn lên một chút vượt qua đứng thẳng và quay trở lại vị trí bắt đầu. Hít thở khi bạn bắt đầu chuyển động và thở ra khi bạn hoàn thành nó.', 'nan', '10', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nặng', 'STANDING LEG RAISE / HIP FLEXION', 'Đứng với lưng hướng về phía dây cáp thấp với cơ thể bạn hơi uốn về phía trước. Nắm chặt vào một cái gì đó để được hỗ trợ hoặc đặt tay lên hông. Gắn dây đàn hồi vào cáp và đưa chân của bạn về phía trước. Thường dễ dàng hơn khi uốn đầu gối nếu bạn muốn tập trung vào cơ bắp đùi trước. Tuy nhiên, cơ iliopsoas vẫn là cơ co bóp mạnh mẽ nhất của cơ flexor hông. Nếu bạn xoay chân ra ngoài (từ hông), bạn sẽ làm cho cơ adductor làm việc nặng nề hơn. Ngược lại, nếu bạn điều hướng chuyển động với một quay vào bên trong, các cơ tham gia vào việc mở chân sẽ đóng góp. Hít thở khi bạn bắt đầu chuyển động và thở ra khi bạn hoàn thành nó.', 'nan', '10', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nặng', 'SMITH MACHINE SPLIT SQUAT', 'Đứng nắm chặt thanh tạ với kiểu cầm làm bằng lòng bàn tay, để nó nghỉ trên cơ vai trapezius và cơ đùi đứng như trong Bài Tập Squad với Máy Smith (Ex. 12.2). Bước lùi và xuống với một chân, đưa đầu gối gần mặt đất, như là bạn đang đi lùi, và để trọng lượng chuyển sang chân phía trước. Giữ cho lưng thẳng và đảm bảo rằng chân phía trước của bạn ở dưới đầu gối. Nâng thanh tạ lên bằng cách đẩy chân phía trước. Hít thở khi bạn đi xuống và thở ra khi bạn hoàn thành chuyển động lên.', 'nan', '10', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nặng', 'BARBELL SQUAT', 'Đứng nhìn về phía trước với chân mở rộng hơn hông một chút và hướng hơi ra ngoài (xoay chân 20°-30º). Nắm thanh tay từ phía trên (cái lòng bàn tay hướng lên) và giữ nó phía sau đầu, nằm trên cơ vai và cơ vai. Hãy uốn đầu gối về phía ngoài (thẳng hàng với chân) cho đến khi đùi gần như song song với sàn nhà. Co bó cơ bụng và cơ lưng dưới. Không nên nhấc gót chân lên khỏi sàn nhà. Nếu cần, bạn có thể đặt một cái gì đó thấp dưới gót chân để hỗ trợ. Hít thở vào khi bạn bắt đầu uốn đầu gối, sau đó giữ hơi và thở ra khi bạn đạt đến đỉnh của chuyển động. Hít thở sâu và lặp lại chuyển động.', 'nan', '10', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Chân (Leags Group)', 'Nặng', 'DEADLIFT', 'Đứng với đôi chân hơi rộng và đôi chân gần như thẳng, nắm thanh tạ từ trên (palms hướng xuống) chạy qua đùi và nhìn thẳng về phía trước. Hãy hơi người về phía trước ở hông để hạ thân dưới mà không làm cung lưng hoặc làm cho thanh tạ rời khỏi cơ thể. Bạn nên cảm thấy áp lực trên gót chân khi bạn xuống, nhưng không phải ở đầu ngón chân. Tập trung vào việc tập trung nỗ lực vào cơ mở rộng hông ở phía sau đùi và không phải ở lưng. Hít thở vào khi bắt đầu hạ trọng lượng và thở ra khi nâng lên. Hít thở sâu trước khi lặp lại động tác.', 'nan', '6', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nhẹ ', 'CRUNCHES', 'Nằm ngửa với đầu gối uốn và chân chạm đất hoặc đặt trên một bàn. Gập tay qua ngực hoặc đặt sau đầu (chạm nhẹ nhưng không giữ chặt). Nâng đầu gối lên bằng cách co bụng một cách ngắn gọn và kiểm soát, đồng thời làm tròn nhẹ lưng. Phần thấp của lưng nên duy trì tiếp xúc với bàn hoặc sàn suốt quá trình tập. Cố gắng giảm khoảng cách giữa xương chậu và ngực. Hít thở vào khi hạ thân hình và thở ra khi nâng lên. (Điều này sẽ cho phép bạn có thêm một vài độ của chuyển động.)', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nhẹ ', 'LYING LEG RAISE', 'Nằm ngửa với đôi chân thẳng ra, có thể là song song hoặc chéo, tay hai bên cơ thể và đặt dưới mông hoặc nắm chặt vào một bàn đặt phía sau đầu. Nâng xương chậu bằng cách đẩy chân lên theo một chuyển động dọc, nâng cao phần thấp của lưng mỗi lần co bụng. Nếu bụng của bạn chưa đủ mạnh, có thể bạn sẽ cảm thấy muốn lắc nhẹ, nhưng lý tưởng là chuyển động nên diễn ra chậm và kiểm soát, giống như bất kỳ bài tập nào khác. Hít thở vào khi hạ chân và thở ra khi nâng chúng lên.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nhẹ ', 'SEATED MACHINE CRUNCH', 'Ngồi trên máy với chân đặt dưới các hỗ trợ và nắm giữ các tay nắm trên đầu. Hãy uốn người về phía trước trong khi kéo các tay nắm hoặc đẩy bằng ngực, tùy thuộc vào thiết kế của máy. Hít thở khi bạn nâng người lên và thở ra khi bạn hạ xuống.', 'nan', '15', '12', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Trung bình', 'ROMAN CHAIR SIT-UP', 'Ngồi trên một bàn được đặt ở một góc khoảng 45º (tạo thành hình chữ V ngược) với chân đặt dưới các địa điểm chống và tay chạm nhẹ (nhưng không giữ chặt) vào đầu hoặc qua ngực. Nâng thân hình lên trong một chuyển động dài hơn so với crunch bằng cách co bụng và cố gắng giảm khoảng cách giữa xương chậu và ngực. Giữ phần thấp của lưng có chút vòi cong suốt quá trình di chuyển. Không cần phải hạ thân hình thấp hơn mặt sàn hoặc nâng cao lên hơn 90º. Hít thở vào khi hạ thân hình và thở ra khi nâng lên.', 'nan', '15', '16', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Trung bình', 'VERTICAL LEG RAISE', 'Dựa vào máy với khuỷu tay và cánh tay nghỉ trên các hỗ trợ của máy và lưng chạm vào bảng hỗ trợ. Gập đầu gối và hông 90º hoặc hơn, sau đó nâng chân lên trong khi giữ vững tư thế này, làm cho phần thân trên cuộn lên và đưa xương chậu gần với ngực. Ý tưởng là giữ cho động tác điều khiển và chậm rãi. Hít thở vào khi hạ chân và thở ra khi nâng chúng lên.', 'nan', '15', '16', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Trung bình', 'INVERTED LEG DROP', 'Thực hiện động tác đứng vai trên một bàn lưng dưới, giữ cơ thể thẳng và cho phép chân bạn giảm xuống một vài độ trước khi nâng chúng lên lại. Thở vào, giữ hơi khi bạn hạ chân và thở ra khi bạn nâng chúng lên.', 'nan', '10', '14', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nặng', 'SEATED LEG TUCK / SCISSORS CRUNCH', 'Ngồi trên bàn phẳng (hoặc trên sàn), đặt tay ngay phía sau mông. Gập chân và hông để đưa đùi lên gần ngực. Phần thân của bạn nên có độ cong nhẹ về phía trước. Duỗi chân cùng một lúc khi đồng thời nghiêng lưng và làm thẳng phần thân. Không để cho chân chạm sàn, mặc dù chúng nên ở gần sàn suốt thời gian. Hoàn thành động tác bằng cách đưa chân và phần thân lên và lại gần nhau. Hít thở vào khi hạ thân trên xuống (mở cơ thể) và thở ra khi đưa lên (đóng cơ thể).', 'nan', '15', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nặng', 'TWISTS', 'Đứng trước gương với chân mở rộng để giữ thăng bằng. Tập trung tầm nhìn phía trước. Đặt một cây gỗ hoặc cây cần khác phía sau đầu và nắm chặt nó bằng cách đặt lên cổ và phía sau cơ vai. Lực kéo cơ bụng một cách chặt chẽ và xoay từ một bên sang bên kia bằng cách sử dụng cơ bụng để kiểm soát phạm vi chuyển động. Mỗi lần xoay nên di chuyển qua một cung đầy đủ khoảng 90º, nhưng không bao giờ đến 180º. (Đầu của cây không bao giờ được chỉ thẳng về phía trước và phía sau.) Hít thở theo những khoảng thời gian ngắn. Thường thì, bạn nên hít thở khi di chuyển về phía trước và thở ra khi quay sang một bên.', 'nan', '20', '20', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nặng', 'SIDE BEND', 'Đứng trước gương với chân mở rộng để giữ thăng bằng. Đặt một thanh tạ phía sau đầu và nắm chặt nó bằng cách đặt lên cổ và phía sau cơ vai. Lực kéo cơ bụng một cách chặt chẽ và hẹn qua trái và phải, sử dụng cùng cơ bụng để kiểm soát phạm vi chuyển động (tối đa khoảng 40º). Hít thở theo những khoảng thời gian ngắn, hít thở khi bạn đứng thẳng lên và thở ra khi bạn hẹn qua.', 'nan', '15', '18', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nặng', 'LATERAL CRUNCH', 'Nằm nghiêng về một bên với đôi chân chéo và, nếu có thể, giữ chúng ở dưới các hỗ trợ của bàn tập cơ bụng phẳng. Đặt tay trên lên đầu và tay dưới qua ngực (khó hơn). Kéo chặt cơ bụng để nâng vai lên một vài inches từ mặt sàn bằng cách uốn cong cơ thể. Hít thở khi bạn nâng cơ thể lên và thở ra khi bạn hạ xuống.', 'nan', '10', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nặng', 'DEADLIFT', 'Đứng với đầu gối hơi uốn và nắm thanh tạ với kiểu cầm từ trên hoặc kiểu cầm trung tính, để nó nằm dọc theo đùi. Đôi chân nên cách nhau khoảng bề rộng của hông. Nghiêng cơ thể về phía trước mà không tách thanh tạ quá xa khỏi cơ thể. Tập trung tâm trí vào cơ erector ở phần thấp của lưng, chứ không phải phần sau đùi. Đảm bảo rằng bạn hạ cơ thể của mình ở một tư thế tự nhiên. Bạn nên cảm thấy áp lực trên gót chân và không phải ở đầu ngón chân. Hít thở khi ở tư thế khởi đầu, giữ hơi thở khi bạn hạ thanh tạ, và thở ra khi bạn hoàn thành cử động nâng tạ. Hít thở sâu trước khi lặp lại cử động.', 'nan', '10', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nặng', 'BACK EXTENSION', 'Đứng trên bàn mở rộng lưng đặt ở góc khoảng 45º với chân dựa vào phần hỗ trợ và xích mông nằm chống lên một gối. Cúi cơ thể của bạn khoảng 90º và sau đó nâng thẳng nó lên trước khi hạ xuống lại. Uốn cong lưng một chút khi hạ cơ thể, điều này sẽ đảm bảo rằng tất cả các cơ lân cận hoạt động khi bạn đứng thẳng trở lại. Hít thở ngắn trước khi thực hiện cử động hạ xuống và thở ra (mà không phải thở hết hơi) khi bạn đứng lên.', 'nan', '10', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nặng', 'HANGING SIT-UP', 'Treo ngược từ một thanh sử dụng dây đeo mắt cá chân. Nâng cơ thể của bạn bằng cách uốn cong từ hông và làm vòm lưng. Thở tự nhiên.', 'nan', '10', '15', '2.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nặng', 'TWISTER', 'Đứng trên đĩa xoay với chân rộng và đầu gối hơi uốn. Nắm chặt tay nắm bằng tay. Hãy co giãn cơ bụng chặt và quay từ một bên sang bên kia bằng cách sử dụng cơ bụng để kiểm soát phạm vi chuyển động. Quay qua một cung khoảng 90º. Hít thở tự nhiên trong những khoảng thời gian ngắn.', 'nan', '20', '20', '3.0');
INSERT INTO exercise_gym (exercise_type, difficulty_level, exercise_name, description, image_link, reps_per_set, kcal_per_set, time_per_set) VALUES ('Bụng và lưng dưới (Abdomen & Lower Back Group)', 'Nặng', 'SEATED-ROW BACK EXTENSION', 'Tư thế giống như Bài tập Kéo dây ngồi (Lưng, Bài tập 10), ngồi đối diện máy với cánh tay duỗi ra phía trước và khuỷu tay hơi uốn và khóa ở vị trí. Giữ chân thẳng hơn so với Kéo dây ngồi, nhưng vẫn hơi uốn ở đầu gối.Bắt đầu với cơ thể thẳng và lưng hơi cong, nghiêng về phía sau một cách chậm rãi và kiểm soát, kéo dài hông và cột sống. Khi bạn đạt được một góc hơi lớn hơn 45º, quay trở lại tư thế xuất phát thẳng đứng. Hít thở trước khi thả trọng lượng, giữ hơi, và thở ra (nhưng không thở hết không khí) khi đạt đến đỉnh của độ nâng. Hít thở sâu và lặp lại.', 'nan', '15', '16', '2.0');